import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ContentPageMultiple = () => {
  const { param2, category } = useParams(); // Assuming param2 is the letter from the URL
  const [content, setContent] = useState([]);

  const letterMapping = {
    A: 'А', B: 'Б', V: 'В', G: 'Г', D: 'Д', E: 'Е', ZH: 'Ж', Z: 'З',
    I: 'И', K: 'К', L: 'Л', M: 'М', N: 'Н', O: 'О', P: 'П', R: 'Р',
    S: 'С', T: 'Т', U: 'У', F: 'Ф', H: 'Х', C: 'Ц', CH: 'Ч', SH: 'Ш',
    SCH: 'Щ', Y: 'Ы', E: 'Э', YU: 'Ю', YA: 'Я',
  };
  
  useEffect(() => {
    const isHero = window.location.pathname.includes('heroes'); // Determine context based on URL
    const russianLetter = letterMapping[param2.toUpperCase()] || 'А';
    let url;
    if (isHero) {
      url = `http://localhost:1337/heroes/letter/${encodeURIComponent(russianLetter)}`; // Hero endpoint
    } else {
      url = `http://localhost:1337/site/biblio/${encodeURIComponent(russianLetter)}`; // Abbreviation endpoint
    }
    fetchData(url);
  }, [param2, category]);

  const fetchData = (url) => {
    fetch(url)
      .then(response => response.json())
      .then(data => {
        console.log("Received data:", data);
        setContent(data);
      })
      .catch(error => console.error('Error fetching content:', error));
  };


  return (
    <div className="generalPages">
      {content.map((item, index) => (
        <div key={index}>
          <h3>{item.a_title}</h3>
          <p dangerouslySetInnerHTML={{ __html: item.a_text }} />
        </div>
      ))}
    </div>
  );
};

export default ContentPageMultiple;
