import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

const ContentPageMultiple = () => {
  const { param1, param2 } = useParams();
  const [categoryContent, setCategoryContent] = useState('');

  useEffect(() => {
    fetchCategoryContent(param1, param2);
  }, [param1, param2]);

  const fetchCategoryContent = (param1, param2) => {
    // Construct URL with the base, param1, and param2
    const baseUrl = 'http://localhost:1337/site'; // Adjust based on your actual backend URL
    const url = `${baseUrl}/${param1}/${param2 || 'A'}`;

    fetch(url)
      .then(response => response.json())
      .then(data => {
        setCategoryContent(data); // Ensure data is set correctly
      })
      .catch(error => console.error('Error fetching content:', error));
  };

  return (
    <div className="generalPages">
      {/* Render content conditionally based on categoryContent state */}
      <h2>{categoryContent.a_title}</h2>
      {/* <div dangerouslySetInnerHTML={{ __html: categoryContent.a_text }} /> */}
    </div>
  );
};

export default ContentPageMultiple;
