import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';

const ContentPageMultiple = () => {
  const { param2 } = useParams();
  const location = useLocation();
  const [content, setContent] = useState([]);

  const letterMapping = {
    A: 'А', B: 'Б', V: 'В', G: 'Г', D: 'Д', E: 'Е', ZH: 'Ж', Z: 'З',
    I: 'И', K: 'К', L: 'Л', M: 'М', N: 'Н', O: 'О', P: 'П', R: 'Р',
    S: 'С', T: 'Т', U: 'У', F: 'Ф', H: 'Х', C: 'Ц', CH: 'Ч', SH: 'Ш',
    SCH: 'Щ', Y: 'Ы', E: 'Э', YU: 'Ю', YA: 'Я',
  };
  
  useEffect(() => {
    // Check if the path includes 'biblio' to determine the nature of the fetch operation
    if (location.pathname.includes('biblio')) {
      // Fetch bibliography articles for the specific letter
      fetchBibliographyArticles(param2);
    } else {
      // Fetch standard articles starting with the letter
      fetchArticleById(param2);
    }
  }, [param2, location.pathname]);
  
  const fetchArticleById = (param2) => {
    console.log("Fetching article with ID:", param2); // Log the ID being fetched
    fetch(`http://localhost:1337/article/${param2}`)
      .then(response => response.json())
      .then(data => {
        console.log("Response from API:", data); // Log the response from the API
        setContent(data ? [data] : []); // Set content to an array containing data if data exists, otherwise set it to an empty array
        console.log("Content after setting state:", content); // Log the content after setting state
      })
      .catch(error => console.error('Error fetching article by ID:', error));
  };

  const fetchBibliographyArticles = (letter) => {
    fetch(`http://localhost:1337/site/biblio/${letter}`)
      .then(response => response.json())
      .then(data => {
        setContent(data); // Set the content state with the fetched data
        // Log the a_text of each fetched article
        data.forEach(article => console.log(article.a_text));
      })
      .catch(error => console.error('Error fetching bibliography articles:', error));
  };
  
  
  
  

  return (
    <div className="generalPages">
      {content.map((item, index) => (
        <div key={index}>
          <h3>{item.a_title}</h3>
          <div dangerouslySetInnerHTML={{ __html: item.a_text }} />
        </div>
      ))}
    </div>
  );
};

export default ContentPageMultiple;
