import React, { useState, useEffect } from "react";
import { Link, useLocation } from 'react-router-dom';
import backgroundImage from '../images/background.png';

const Navbar = () => {
  const [categories, setCategories] = useState([]);
  const location = useLocation();

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = () => {
    fetch('http://localhost:1337/categories/1')
      .then(response => response.json())
      .then(data => {
        setCategories(data);
      })
      .catch(error => console.error('Error fetching categories:', error));
  };

  const handleCategoryClick = (parentId) => {
    // Fetching subcategories based on parentId
    fetch(`http://localhost:1337/subcategories/${parentId}`)
      .then(response => response.json())
      .then(subCategories => {
        console.log('Subcategories:', subCategories);
      })
      .catch(error => console.error('Error fetching subcategories:', error));
  };

  return (
    <div className="navigation">
      <img src={backgroundImage} alt="Background" className="backgroundImageTwo"/>
      <div className="navLinks">
        {categories.map((category, index) => (
          <div key={index} onClick={() => handleCategoryClick(category.c_id)}>
            <Link to={`/site/${category.c_name}`} className="navLink">
              {category.c_name_rus}
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Navbar;
