app.get('/paths/:pathOne/:pathTwo', (req, res) => {
    const { pathOne, pathTwo } = req.params;
    console.log('Path name:', pathOne, pathTwo); // Log the path parameters
    connection.query(
      'SELECT p_name_rus FROM path WHERE p_name = ?',
      [`/${pathOne}/${pathTwo}/`], // Pass the concatenated path directly
      (error, results) => {
        if (error) {
          console.error('Error executing query:', error);
          res.status(500).json({ error: 'Internal server error' });
          return;
        }
        res.json(results);
      }
    );
  });
