import React, { useState, useEffect } from "react";
import { Link, useLocation } from 'react-router-dom';
import backgroundImage from '../images/background.png';
import whiteStar from '../images/whiteStar.png';
import redStar from '../images/redStar.png';

const Navbar = () => {
  const [categories, setCategories] = useState([]);
  const [activeCategoryId, setActiveCategoryId] = useState(null);
  const [subCategories, setSubCategories] = useState([]);
  const location = useLocation();

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = () => {
    fetch('http://localhost:1337/categories/1')
      .then(response => response.json())
      .then(data => {
        setCategories(data);
      })
      .catch(error => console.error('Error fetching categories:', error));
  };

  const handleCategoryClick = (parentId) => {
    // Check if the clicked category is already active, if so, deactivate it (toggle functionality)
    if (activeCategoryId === parentId) {
      setActiveCategoryId(null);
      setSubCategories([]); // Clear subcategories
      return;
    }

    setActiveCategoryId(parentId); // Set active category ID

    // Fetching subcategories based on parentId
    fetch(`http://localhost:1337/subcategories/${parentId}`)
      .then(response => response.json())
      .then(subCategories => {
        setSubCategories(subCategories); // Set fetched subcategories
      })
      .catch(error => console.error('Error fetching subcategories:', error));
  };

  const currentPath = location.pathname.split('/')[2];

  const getStarImage = (categoryName) => {
    return currentPath === categoryName ? whiteStar : redStar;
  };

  return (
    <div className="navigation">
      <img src={backgroundImage} alt="Background" className="backgroundImageTwo"/>
      <div className="navLinks">
        {categories.map((category, index) => (
          <React.Fragment key={index}>
            <div className="linkWithStar" onClick={() => handleCategoryClick(category.c_id)}>
              <Link to={`/site/${category.c_name}`} className="navLink">
                {category.c_name_rus}
              </Link>
              <img src={getStarImage(category.c_name)} alt="Star" className={currentPath === category.c_name ? "starIconWhite" : "starIconRed"} />
            </div>
            {/* Conditional rendering of subcategories based on the active category ID */}
            {activeCategoryId === category.c_id && subCategories.map((subCategory, subIndex) => (
              <div key={`sub-${subIndex}`} className="linkWithStar subLink">
                <Link to={`/site/${subCategory.c_name}`} className="navLink">
                  {subCategory.c_name_rus}
                </Link>
                <img src={redStar} alt="Sub Star" className="starIconRed" />
              </div>
            ))}
          </React.Fragment>
        ))}
      </div>
    </div>
  );




};

export default Navbar;
