import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const SearchResults = () => {
  const [soldiers, setSoldiers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const location = useLocation();

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const surname = queryParams.get('surname');
    const name = queryParams.get('name');
    const fathername = queryParams.get('fathername');
    const page = queryParams.get('page') || 1;

    fetch(`http://localhost:1337/soldiers/search?surname=${surname}&name=${name}&fathername=${fathername}&page=${page}`)
      .then(response => response.json())
      .then(data => {
        setSoldiers(data.results);
        setTotalPages(data.totalPages);
        setCurrentPage(data.currentPage);
      })
      .catch(error => console.error('Error fetching search results:', error));
  }, [location.search]);

  const renderPagination = () => {
    let startPage = Math.max(currentPage - 5, 1);
    let endPage = Math.min(startPage + 9, totalPages);

    const pages = [];
    for (let i = startPage; i <= endPage; i++) {
      pages.push(i);
    }

    return (
      <div className="pagination">
        {pages.map(page => (
          <button key={page} disabled={page === currentPage} onClick={() => changePage(page)}>
            {page}
          </button>
        ))}
      </div>
    );
  };

  const changePage = (newPage) => {
    const queryParams = new URLSearchParams(location.search);
    queryParams.set('page', newPage);
    window.location.search = queryParams.toString();
  };

  return (
    <div className="generalPages">
      <h1>Search Results</h1>
      <ul>
        {soldiers.map((soldier, index) => (
          <li key={index}>
            {/* Display soldier details */}
            {soldier.s_surname}, {soldier.s_name} {soldier.s_fathername} - {soldier.s_birthyear}
          </li>
        ))}
      </ul>
      {renderPagination()}
    </div>
  );
};

export default SearchResults;
