import React, { useState, useEffect } from "react";
import { useParams, Link, useNavigate, useLocation } from 'react-router-dom';
import queryString from 'query-string';

const ContentPage = () => {
  const { pathName} = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const [pathInfo, setPathInfo] = useState(null);
  const [articles, setArticles] = useState([]);
  const [files, setFiles] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [soldiers, setSoldiers] = useState([]);

  const alphabet = 'АБВГДЕЖЗИКЛМНОПРСТУФХЦЧШЩЫЭЮЯ'.split('');
  const englishAlphabet = ['A', 'B', 'V', 'G', 'D', 'E', 'ZH', 'Z', 'I', 'K', 'L', 'M', 'N', 'O', 'P', 'R', 'S', 'T', 'U', 'F', 'H', 'C', 'CH', 'SH', 'SCH', 'Y', 'E', 'YU', 'YA'];

  const { page = 1 } = queryString.parse(location.search);
  const {letter} = queryString.parse(location.match)

  const [currentPage, setCurrentPage] = useState(page || 1);

  useEffect(() => {
    fetchPathDetails(pathName);
    fetchSubCategories(pathName);
    if (pathName.includes('heroes')) { // Modify if necessary to correctly identify the 'heroes' path
      fetchSoldiers(page);
    }
    if (letter) {
      fetchSoldiersByLetter(letter);
    }
  }, [pathName, currentPage, letter, page]);

  const fetchSoldiers = (page) => {
    fetch(`http://localhost:1337/soldiers/${page}`)
      .then(response => response.json())
      .then(data => {
        setSoldiers(data); // Directly use data as it is the array you need
        // Assuming the API doesn't return totalPages anymore since that was part of the initial incorrect assumption
      })
      .catch(error => console.error('Error fetching soldiers:', error));
  };
  

  const fetchPathDetails = (pathName) => {
    fetch(`http://localhost:1337/path-details/${pathName}`)
      .then(response => response.json())
      .then(data => {
        setPathInfo(data);
        if (data && data.articles) {
          setArticles(data.articles);
        }
        if (data && data.files) {
          setFiles(data.files);
        }
      })
      .catch(error => console.error('Error fetching path details:', error));
  };

  const fetchSubCategories = (pathName) => {
    fetch(`http://localhost:1337/subcategories/${pathName}`)
      .then(response => response.json())
      .then(data => {
        setSubCategories(data.map(category => ({
          ...category,
          link: `/site${category.link.replace(/^\/|\/$/g, '')}`
        })));
      })
      .catch(error => console.error('Error fetching subcategories:', error));
  };

  // Function to determine if special layout is needed
  const isSpecialLayoutNeeded = () => {
    // Assuming pathInfo.c_id is set correctly from fetchPathDetails
    return pathInfo && (pathInfo.c_id === 45 || pathInfo.c_id === 11);
  };

  const fetchSoldiersByLetter = (letter) => {
    fetch(`http://localhost:1337/soldiers/letter/${letter}`) // Adjust the API URL as needed
      .then(response => response.json())
      .then(data => {
        setSoldiers(data);
      })
      .catch(error => console.error('Error fetching soldiers:', error));
  };

  const renderAlphabetNavigation = () => (
    <div className="alphabet-navigation">
      {englishAlphabet.map((letter, index) => (
        <Link key={index} to={`/site/heroes/letter/${letter}`}>
          {letter}
        </Link>
      ))}
    </div>
  );

  const changePage = (newPage) => {
    navigate(`/site/heroes?page=${newPage}`);
  };

  const renderPagination = () => {
    const totalNumberOfPages = 10; // This should come from your API response if possible
    return (
      <div className="pagination">
        {[...Array(totalNumberOfPages)].map((_, index) => (
          <button key={index} onClick={() => changePage(index + 1)}>
            {index + 1}
          </button>
        ))}
      </div>
    );
  };
  

  return (
    <div className="generalPages">
      {pathInfo && !pathName.includes('heroes') ? (
        <div>
          <h2>{pathInfo.title}</h2>
          <ul className={`subCategoryList ${isSpecialLayoutNeeded() ? 'specialLayout' : ''}`}>
            {subCategories.map((category, index) => (
              <li key={index}>
                <a href={category.link}>{category.c_name_rus}</a>
              </li>
            ))}
          </ul>
          {articles.map((article, index) => (
            <div key={index}>
              <h3>{article.a_title}</h3>
              <div dangerouslySetInnerHTML={{ __html: article.a_text }}></div>
            </div>
          ))}
          <ul>
            {files.map((file, index) => (
              <li key={index}>
                <a href={file.http_path}>{file.title}</a>
              </li>
            ))}
          </ul>
        </div>
      ) : null}
      {pathName.includes('heroes') && (
        <>
          <h2>Герои Советского Союза</h2>
      {renderAlphabetNavigation()}
      <ul>
        {soldiers.map((soldier, index) => (
          <li key={index}>
            <h3>{soldier.a_title}</h3>
            {/* <div dangerouslySetInnerHTML={{ __html: soldier.a_text }} /> */}
          </li>
        ))}
          </ul>
        </>
      )}
    </div>
  );


};

export default ContentPage;
