import React, { useState, useEffect } from "react";
import { useParams, Link } from 'react-router-dom';

const ContentPage = () => {
  const { page } = useParams(); // Assuming you use react-router and have a route setup for /soldiers/:page
  const [soldiers, setSoldiers] = useState([]);
  const [currentPage, setCurrentPage] = useState(page || 1);

  useEffect(() => {
    fetchSoldiers(currentPage);
  }, [currentPage]);

  const fetchSoldiers = (page) => {
    fetch(`http://localhost:1337/soldiers/${page}`)
      .then(response => response.json())
      .then(data => {
        setSoldiers(data);
      })
      .catch(error => console.error('Error fetching soldiers:', error));
  };

  const renderPagination = () => {
    // This is a basic pagination component. You may want to fetch total count from the server to improve.
    const pages = [...Array(5).keys()].map(n => n + 1); // Example for 5 pages
    return (
      <div className="pagination">
        {pages.map(n => (
          <Link key={n} to={`/soldiers/${n}`} onClick={() => setCurrentPage(n)}>
            {n}
          </Link>
        ))}
      </div>
    );
  };

  return (
    <div className="generalPages">
      <h2>Soldiers</h2>
      {soldiers.map((soldier, index) => (
        <div key={index}>
          <h3>{soldier.a_title}</h3>
          <div dangerouslySetInnerHTML={{ __html: soldier.a_text }}></div>
        </div>
      ))}
      {renderPagination()}
    </div>
  );
};

export default ContentPage;
