import React, { useState, useEffect } from 'react';
import { homeUrlTitle } from '../apiConfig';
import MainPage from '../images/mainPage.jpg';

const Home = () => {
  const [homeText, setHomeText] = useState('');

  useEffect(() => {
    fetch(homeUrlTitle)
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data) && data.length > 0) {
          // Replace the image source with MainPage
          let modifiedText = data[0].a_text;
          // Filter out the unwanted image
          modifiedText = modifiedText.replace(/<img[^>]*src=["'][^"']*\/spacer\.gif["'][^>]*>/gi, '');
          // Replace only the spacer.gif image source
          modifiedText = modifiedText.replace(/src=["'][^"']*\/spacer\.gif["']/gi, `src="${MainPage}"`);
          setHomeText(modifiedText);
        }
      })
      .catch((error) => console.error('Error fetching articles:', error));
  }, []);
  
  

  return (
    <div className="generalPages">
      <div dangerouslySetInnerHTML={{ __html: homeText }} />
    </div>
  );
};

export default Home;
