import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

const ContentPage = () => {
  const { categoryId } = useParams();
  const [categoryContent, setCategoryContent] = useState('');

  useEffect(() => {
    fetchCategoryContent(categoryId);
  }, [categoryId]);

  const fetchCategoryContent = (categoryId) => {
    // Fetch category content based on categoryId
    fetch(`http://localhost:1337/categories/${categoryId}`)
      .then(response => response.json())
      .then(data => {
        setCategoryContent(data);
      })
      .catch(error => console.error('Error fetching category content:', error));
  };

  return (
    <div className="content">
      <h2>Category Content</h2>
      <p>{categoryContent}</p>
    </div>
  );
};

export default ContentPage;
