import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import Navigation from '../images/background.png';
import RedStar from '../images/redStar.png'
import WhiteStar from '../images/whiteStar.png'

const Navbar = () => {
  const location = useLocation();
  const [activePage, setActivePage] = useState(""); // State to store active page

  // Function to handle page changes
  const handlePageChange = (page) => {
    setActivePage(page);
  };

  // Clear active page when navigating back to the main page ("/")
  useEffect(() => {
    if (location.pathname === "/") {
      setActivePage("");
    }
  }, [location.pathname]);

  return (
    <div className="navigation">
        <div className="backgroundImageTwo">
            <img src={Navigation} alt="Background Image" />
        </div>
        <div className="navLinks">
            <div className="linkContainer">
                <Link
                to="/About"
                onClick={() => handlePageChange("About")}
                className={activePage === "About" ? "active" : ""}
                >
                <span>База данных</span>
                {activePage === "About" ? <img src={WhiteStar} alt="Red Star" /> : <img src={RedStar} alt="Black Star" />}
                </Link>
            </div>
            <div className="linkContainer">
                <Link
                to="/Links"
                onClick={() => handlePageChange("Links")}
                className={activePage === "Links" ? "active" : ""}
                >
                <span>Описание боевых действий</span>
                {activePage === "Links" ? <img src={WhiteStar} alt="Red Star" /> : <img src={RedStar} alt="Black Star" />}
                </Link>
            </div>
            <div className="linkContainer">
                <Link
                to="/Feedback"
                onClick={() => handlePageChange("Feedback")}
                className={activePage === "Feedback" ? "active" : ""}
                >
                <span>Список частей и соединений</span>
                {activePage === "Feedback" ? <img src={WhiteStar} alt="Red Star" /> : <img src={RedStar} alt="Black Star" />}
                </Link>
            </div>
            <div className="linkContainer">
                 
            </div>
        </div>

    </div>
);
};

export default Navbar;
