import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import Navigation from '../images/background.png';
import RedStar from '../images/redStar.png'

const Navbar = () => {
  const location = useLocation();
  const [activePage, setActivePage] = useState(""); // State to store active page

  // Function to handle page changes
  const handlePageChange = (page) => {
    setActivePage(page);
  };

  // Clear active page when navigating back to the main page ("/")
  useEffect(() => {
    if (location.pathname === "/") {
      setActivePage("");
    }
  }, [location.pathname]);

  return (
    <div className="navigation">
        <div className="backgroundImageTwo">
            <img src={Navigation} alt="Background Image" />
        </div>
        <div className="navLinks">
    <Link
      to="/About"
      onClick={() => handlePageChange("About")}
      className={activePage === "About" ? "active" : ""}
    >
      About {activePage === "About" ? <img src={RedStar} alt="Red Star" /> : <img src={BlackStar} alt="Black Star" />}
    </Link>
    <Link
      to="/Links"
      onClick={() => handlePageChange("Links")}
      className={activePage === "Links" ? "active" : ""}
    >
      Links {activePage === "Links" ? <img src={RedStar} alt="Red Star" /> : <img src={BlackStar} alt="Black Star" />}
    </Link>
    <Link
      to="/Feedback"
      onClick={() => handlePageChange("Feedback")}
      className={activePage === "Feedback" ? "active" : ""}
    >
      Feedback {activePage === "Feedback" ? <img src={RedStar} alt="Red Star" /> : <img src={BlackStar} alt="Black Star" />}
    </Link>
  </div>
    </div>
);
};

export default Navbar;
