import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ContentPageMultiple = () => {
  const { param2, a_id } = useParams(); // Assuming param2 is the letter from the URL
  const [content, setContent] = useState([]);

  const letterMapping = {
    A: 'А', B: 'Б', V: 'В', G: 'Г', D: 'Д', E: 'Е', ZH: 'Ж', Z: 'З',
    I: 'И', K: 'К', L: 'Л', M: 'М', N: 'Н', O: 'О', P: 'П', R: 'Р',
    S: 'С', T: 'Т', U: 'У', F: 'Ф', H: 'Х', C: 'Ц', CH: 'Ч', SH: 'Ш',
    SCH: 'Щ', Y: 'Ы', E: 'Э', YU: 'Ю', YA: 'Я',
  };
  
  useEffect(() => {
    if (a_id) {
      fetchArticleById(a_id);
    } else {
      fetchArticleById(a_id);
    }
  }, [param2, a_id]);
  
  const fetchArticleById = (a_id) => {
    fetch(`http://localhost:1337/site/article/${a_id}`)
      .then(response => response.json())
      .then(data => {
        console.log("Received data:", data); // Log the entire data array
        setContent([data]); // Wrap the data in an array and set it to the state
      })
      .catch(error => console.error('Error fetching article by ID:', error));
  };
  
  const fetchData = (russianLetter) => {
    const url = `http://localhost:1337/site/biblio/${encodeURIComponent(russianLetter)}`;
    fetch(url)
      .then(response => response.json())
      .then(data => {
        setContent(data);
      })
      .catch(error => console.error('Error fetching content:', error));
  };

  console.log("Content:", content); // Log content outside JSX

  return (
    <div className="generalPages">
      {content.map((item, index) => (
        <div key={index}>
          <h3>{item.a_title}</h3>
          <div dangerouslySetInnerHTML={{ __html: item.a_text }} />
        </div>
      ))}
    </div>
  );
};

export default ContentPageMultiple;
