import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import {contentMultipleCategoryContent} from './apiConfig'

const ContentPageMultiple = () => {
  const { param1, param2 } = useParams();
  const [content, setContent] = useState({a_title: '', a_text: ''});

  useEffect(() => {
    const letter = param2 || ''; // If param2 is not defined, default to empty string
    fetchCategoryContent(param1, letter);
  }, [param1, param2]);

  const fetchCategoryContent = (param1, letter) => {
    const url = letter ? `${contentMultipleCategoryContent}/${param1}/${letter}` : `${contentMultipleCategoryContent}/${param1}`;
    fetch(url)
      .then(response => response.json())
      .then(data => {
        setContent(data);
      })
      .catch(error => console.error('Error fetching content:', error));
  };

  return (
    <div className="generalPages">
      <h2>{content.a_title}</h2>
      <div dangerouslySetInnerHTML={{ __html: content.a_text }}></div>
    </div>
  );
};


export default ContentPageMultiple;
