import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';

const ContentPageMultiple = () => {
  const { param1, param2 } = useParams();
  const location = useLocation();
  const [content, setContent] = useState([]);
  
  const letterMappingAbbreviations = {
  A: 'А', B: 'Б', V: 'В', G: 'Г', D: 'Д', J: 'Е', ZH: 'Ж', Z: 'З',
  I: 'И', K: 'К', L: 'Л', M: 'М', N: 'Н', O: 'О', P: 'П', R: 'Р',
  S: 'С', T: 'Т', U: 'У', F: 'Ф', H: 'Х', C: 'Ц', CH: 'Ч', SH: 'Ш',
  SCH: 'Щ', Y: 'Ы', E: 'Э', YU: 'Ю', YA: 'Я', };
  const letterMappingBibliography = {
    A: 'А', B: 'Б', V: 'В', G: 'Г', D: 'Д', E: 'Е', ZH: 'Ж', Z: 'З',
    I: 'И', K: 'К', L: 'Л', M: 'М', N: 'Н', O: 'О', P: 'П', R: 'Р',
    S: 'С', T: 'Т', U: 'У', F: 'Ф', H: 'Х', C: 'Ц', CH: 'Ч', SH: 'Ш',
    SCH: 'Щ', Y: 'Ы', YE: 'Э', YU: 'Ю', YA: 'Я', };

    const fetchContent = (paramOne, paramTwo) => {
      const url = `http://localhost:3000/paths/${paramOne}/${paramTwo}`;
      console.log('Fetching content for URL:', url); // Log the URL being fetched
    
      fetch(url)
        .then(response => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then(data => {
          console.log('Data received:', data); // Log the received data
          // Set state with the data or handle it as needed
        })
        .catch(error => {
          console.error('Fetch error:', error); // Log any fetch error
        });
    };

    useEffect(() => {
      if (location.pathname.includes('/parts/')) {
        const [_, paramOne, paramTwo] = location.pathname.split('/');
        fetchContent(paramOne, paramTwo);
      }
    }, [location.pathname]);

  const fetchAbbreviations = (letter) => {
    const russianLetter = letterMappingAbbreviations[letter.toUpperCase()] || letter;
    fetch(`http://localhost:1337/site/abbreviation/${russianLetter}`)
      .then(response => response.json())
      .then(data => {
        console.log(`Fetching from URL: http://localhost:1337/site/abbreviation/${russianLetter}`);
        setContent(data);
      })
      .catch(error => console.error('Error fetching abbreviations:', error));
  };
  
  const fetchArticleById = (param2) => {
    console.log("Fetching article with ID:", param2);
    fetch(`http://localhost:1337/${param1}/${param2}`)
      .then(response => response.json())
      .then(data => {
        console.log("Response from API:", data);
        setContent(data ? [data] : []);
      })
      .catch(error => console.error('Error fetching article by ID:', error));
  };

  const fetchBibliographyArticles = (letter) => {
    // Assuming letterMapping translates English letter to Russian for fetching
    const russianLetter = letterMappingBibliography[letter.toUpperCase()] || letter;
    fetch(`http://localhost:1337/site/biblio/${russianLetter}`)
      .then(response => response.json())
      .then(data => {
        setContent(data);
      })
      .catch(error => console.error('Error fetching bibliography articles:', error));
  };

  return (
    <div className="generalPages">
      {content.map((item, index) => (
        <div key={index}>
          <h3>{item.a_title}</h3>
          <div dangerouslySetInnerHTML={{ __html: item.a_text }} />
        </div>
      ))}
    </div>
  );
};

export default ContentPageMultiple;
