import React, { useState, useEffect } from "react";
import RedStar from '../images/redStar.png';
import WhiteStar from '../images/whiteStar.png';
import { Link, useLocation } from 'react-router-dom';

const Navbar = () => {
  const [categories, setCategories] = useState([]);
  const location = useLocation();

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = () => {
    fetch('http://localhost:1337/categories/1') // Fetch categories with parent_id 1
      .then(response => response.json())
      .then(data => {
        setCategories(data);
      })
      .catch(error => console.error('Error fetching categories:', error));
  };

  return (
    <div className="navigation">
      <div className="linkContainer">
        {categories.map(category => (
          <Link
            key={category.c_id}
            to={category.c_name}
            className={location.pathname.startsWith(category.c_name) ? 'active' : ''}
          >
            <span className="linkText">{category.c_name_rus}</span>
            <img
              className="starIcon"
              src={location.pathname === category.c_name ? WhiteStar : RedStar}
              alt="Star Icon"
            />
          </Link>
        ))}
      </div>
      {/* Other Navbar content */}
    </div>
  );
};

export default Navbar;
