import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

const ContentPage = () => {
  const { pathName } = useParams();
  const [pathInfo, setPathInfo] = useState(null);
  const [articleText, setArticleText] = useState(null);
  const [subCategories, setSubCategories] = useState([]);

  useEffect(() => {
    // Fetch path details and subcategories when pathName changes
    fetchPathDetails(pathName);
    fetchSubCategories(pathName);
  }, [pathName]);

  const fetchPathDetails = (pathName) => {
    fetch(`http://localhost:1337/path-details/${pathName}`)
      .then(response => response.json())
      .then(data => {
        console.log('Path Details from Backend:', data);
        setPathInfo(data);
        if (data && data.text) {
          setArticleText(data.text);
        } else {
          setArticleText("No article text available");
        }
      })
      .catch(error => console.error('Error fetching path details:', error));
  };

  const fetchSubCategories = (pathName) => {
    fetch(`http://localhost:1337/subcategories/${pathName}`)
      .then(response => response.json())
      .then(data => {
        console.log('Subcategories from Backend:', data);
        const currentPathSegments = window.location.pathname.split('/'); // Split the current URL into segments
        const currentPath = currentPathSegments[currentPathSegments.length - 1]; // Get the last segment of the current URL
        setSubCategories(data.map(category => ({
          ...category,
          link: `/site/${currentPath}` // Construct the link by appending the last segment of the current URL
        })));
      })
      .catch(error => console.error('Error fetching subcategories:', error));
  };
  

  return (
    <div className="generalPages">
      {pathInfo ? (
        <div>
          <h2>{pathInfo.title}</h2>
          <ul>
            {subCategories.map((category, index) => (
              <li key={index}>
                <a href={category.link}>{category.c_name_rus}</a>
              </li>
            ))}
          </ul>
          <div dangerouslySetInnerHTML={{ __html: articleText }}></div>
          
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ContentPage;
