import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ContentPageMultiple = () => {
  const { param2, a_id } = useParams(); // Assuming param2 is the letter from the URL
  const [content, setContent] = useState([]);

  const letterMapping = {
    A: 'А', B: 'Б', V: 'В', G: 'Г', D: 'Д', E: 'Е', ZH: 'Ж', Z: 'З',
    I: 'И', K: 'К', L: 'Л', M: 'М', N: 'Н', O: 'О', P: 'П', R: 'Р',
    S: 'С', T: 'Т', U: 'У', F: 'Ф', H: 'Х', C: 'Ц', CH: 'Ч', SH: 'Ш',
    SCH: 'Щ', Y: 'Ы', E: 'Э', YU: 'Ю', YA: 'Я',
  };
  
  useEffect(() => {
    const russianLetter = letterMapping[param2.toUpperCase()] || 'А';
    fetchData(russianLetter);
    if (a_id) {
      fetchArticleById(a_id); // Fetch a specific article by ID
    } else {
      const russianLetter = letterMapping[param2?.toUpperCase()] || 'А';
      fetchData(russianLetter); // Fetch articles by the first letter
    }
  }, [param2,]);

  const fetchData = (russianLetter) => {
    const url = `http://localhost:1337/site/biblio/${encodeURIComponent(russianLetter)}`;
    fetch(url)
      .then(response => response.json())
      .then(data => {
        console.log("Received data:", data); // Log the entire data array
        data.forEach(item => {
          console.log("Title:", item.a_title); // Log the title of each item
          console.log("Text:", item.a_text); // Log the text content of each item
        });
        setContent(data);
      })
      .catch(error => console.error('Error fetching content:', error));
};


  return (
    <div className="generalPages">
      {content.map((item, index) => (
        <div key={index}>
          {/* <h3>{item.a_title}</h3> */}
          <h3>temp</h3>
          <p dangerouslySetInnerHTML={{ __html: item.a_text }} />
        </div>
      ))}
    </div>
  );
};

export default ContentPageMultiple;
