import React, { useState, useEffect } from "react";
import { useParams, Link, useNavigate, useLocation } from 'react-router-dom';
import queryString from 'query-string';

const ContentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { pathName } = useParams();
  const { page = 1, letter } = queryString.parse(location.search);
  const [pathInfo, setPathInfo] = useState(null);
  const [articles, setArticles] = useState([]);
  const [files, setFiles] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [soldiers, setSoldiers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalNumberOfPages, setTotalNumberOfPages] = useState(0); 
  const [literature, setLiterature] = useState([]) 
  

  useEffect(() => {
    fetchPathDetails(pathName);
    fetchSubCategories(pathName);

    if(location.pathname === '/site/biblio') {
      navigate('/site/biblio/A');
    }
    
    if (pathName.includes('heroes')) {
      if (letter) {
        fetchSoldiersByLetter(letter);
      } else {
        fetchSoldiers(page);
      }
    }

    if (pathName.includes('lit')) {
      fetchLiterature();
    }

    return () => {
      setFiles([]);
    };
  }, [pathName, currentPage, location.search]);

  const fetchLiterature = () => {
    fetch(`http://localhost:1337/literature`)
      .then(response => response.json())
      .then(data => {
        setLiterature(data);
      })
      .catch(error => console.error('Error fetching literature:', error));
  };

  const fetchSoldiers = (currentPage) => {
    fetch(`http://localhost:1337/heroes/${currentPage}`)
      .then(response => response.json())
      .then(({ results, totalPages }) => {
        setSoldiers(results); // Update to use 'results'
        setTotalNumberOfPages(totalPages); // State variable to hold total pages
      })
      .catch(error => console.error('Error fetching soldiers:', error));
  };

  const fetchSoldiersByLetter = (currentLetter) => {
    fetch(`http://localhost:1337/soldiers/letter/${currentLetter}`)
      .then(response => response.json())
      .then(data => setSoldiers(data))
      .catch(error => console.error('Error fetching soldiers:', error));
  };

  const fetchPathDetails = (pathName) => {
    fetch(`http://localhost:1337/path-details/${pathName}`)
      .then(response => response.json())
      .then(data => {
        setPathInfo(data);
        if (data && data.articles) {
          setArticles(data.articles);
        }
        if (data && data.files) {
          setFiles(data.files);
        }
      })
      .catch(error => console.error('Error fetching path details:', error));
  };

  const fetchSubCategories = (pathName) => {
    fetch(`http://localhost:1337/subcategories/${pathName}`)
      .then(response => response.json())
      .then(data => {
        setSubCategories(data.map(category => ({
          ...category,
          link: `/site${category.link.replace(/^\/|\/$/g, '')}`
        })));
      })
      .catch(error => console.error('Error fetching subcategories:', error));
  };

    // Function to determine if special layout is needed
    const isSpecialLayoutNeeded = () => {
      // Assuming pathInfo.c_id is set correctly from fetchPathDetails
      return pathInfo && (pathInfo.c_id === 45 || pathInfo.c_id === 11);
    };

    const changePage = (newPage) => {
      // Construct the URL with both page and letter parameters if letter exists
      const searchParams = letter ? `?page=${newPage}&letter=${letter}` : `?page=${newPage}`;
      navigate(`/site/heroes${searchParams}`);
    };

    const renderPagination = () => {
      return (
        <div className="pagination">
          {[...Array(totalNumberOfPages)].map((_, index) => (
            <button key={index} onClick={() => changePage(index + 1)}>
              {index + 1}
            </button>
          ))}
        </div>
      );
    };


  return (
    <div className="generalPages">
      {pathInfo && !pathName.includes('heroes') && !pathName.includes('lit') ? (
        <div>
          <h2>{pathInfo.title}</h2>
          <ul className={`subCategoryList ${isSpecialLayoutNeeded() ? 'specialLayout' : ''}`}>
            {subCategories.map((category, index) => (
              <li key={index}>
                <a href={category.link}>{category.c_name_rus}</a>
              </li>
            ))}
          </ul>
          {articles.map((article, index) => (
            <div key={index}>
              <div dangerouslySetInnerHTML={{ __html: article.a_text }}></div>
            </div>
          ))}
          <ul>
            {files.map((file, index) => (
              <li key={index}>
                <a href={file.http_path}>{file.title}</a>
              </li>
            ))}
          </ul>
        </div>
      ) : null}



      
{pathName.includes('lit') && (
  <>
    <h2>Литература о войне</h2>
    <ul className="litList">
      {literature.map((item, index) => (
        <li key={index}>
          <Link to={`/site/article/${item.a_id}`}>
            {/* Using span to inline title with the bullet */}
            <span dangerouslySetInnerHTML={{ __html: item.a_title.trim() }} />
          </Link>
          {/* Display description under the title */}
          {/* Use div or p as needed, depending on your layout requirements */}
          <div dangerouslySetInnerHTML={{ __html: item.a_desc.trim() }} />
        </li>
      ))}
    </ul>
  </>
)}


      {pathName.includes('heroes') && (
        <>
          <h2>Герои Советского Союза</h2>
          <ul className="heroesList">
            {soldiers.map((soldier, index) => (
              <li key={index}>
                <Link to={`/site/article/${soldier.a_id}`}>
                  {soldier.a_title}
                </Link>
              </li>
            ))}
          </ul>
          {renderPagination()}
        </>
      )}
    </div>
  );
  
};

export default ContentPage;
