import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

const ContentPage = () => {
  const { pathName } = useParams();
  const [pathInfo, setPathInfo] = useState(null);
  const [articles, setArticles] = useState([]);
  const [files, setFiles] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0); // Assuming you know or can calculate this
  const [heroes, setHeroes] = useState([]);

  useEffect(() => {
    fetchPathDetails(pathName);
    fetchSubCategories(pathName);
    fetchHeroes();

    return () => {
      setFiles([]);
    };
  }, [currentPage, pathName]);

  const fetchHeroes = () => {
    const limit = 15; // Items per page
    fetch(`http://localhost:1337/site/biblio/${pathName}?page=${currentPage}&limit=${limit}`)
      .then(response => response.json())
      .then(data => {
        setHeroes(data);
        // Optionally, update totalPages here if your API returns total count or total pages
      })
      .catch(error => console.error('Error fetching heroes:', error));
  };

  const fetchPathDetails = (pathName) => {
    fetch(`http://localhost:1337/path-details/${pathName}`)
      .then(response => response.json())
      .then(data => {
        setPathInfo(data);
        if (data && data.articles) {
          setArticles(data.articles);
        }
        if (data && data.files) {
          setFiles(data.files);
        }
      })
      .catch(error => console.error('Error fetching path details:', error));
  };

  const fetchSubCategories = (pathName) => {
    fetch(`http://localhost:1337/subcategories/${pathName}`)
      .then(response => response.json())
      .then(data => {
        setSubCategories(data.map(category => ({
          ...category,
          link: `/site${category.link.replace(/^\/|\/$/g, '')}`
        })));
      })
      .catch(error => console.error('Error fetching subcategories:', error));
  };

  // Function to determine if special layout is needed
  const isSpecialLayoutNeeded = () => {
    // Assuming pathInfo.c_id is set correctly from fetchPathDetails
    return pathInfo && (pathInfo.c_id === 45 || pathInfo.c_id === 11);
  };

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  return (
    <div className="generalPages">
      {pathInfo ? (
        <div>
          <h2>{pathInfo.title}</h2>
          {/* Apply classes conditionally based on isSpecialLayoutNeeded */}
          <ul className={`subCategoryList ${isSpecialLayoutNeeded() ? 'specialLayout' : ''}`}>
            {subCategories.map((category, index) => (
              <li key={index}>
                <a href={category.link}>{category.c_name_rus}</a>
              </li>
            ))}
          </ul>
          {articles.map((article, index) => (
            <div key={index}>
              <h3>{article.a_title}</h3>
              <div dangerouslySetInnerHTML={{ __html: article.a_text }}></div>
            </div>
          ))}
          <ul>
            {files.map((file, index) => (
              <li key={index}>
                <a href={file.http_path}>{file.title}</a>
              </li>
            ))}
          </ul>
          
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ContentPage;
