import React, { useState, useEffect } from "react";
import { Link, useLocation } from 'react-router-dom';
import backgroundImage from '../images/background.png'; // Make sure the path is correct
import whiteStar from '../images/whiteStar.png';
import redStar from '../images/redStar.png';

const Navbar = () => {
  const [categories, setCategories] = useState([]);
  const location = useLocation(); // Hook to access the current location

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = () => {
    fetch('http://localhost:1337/categories/1')
      .then(response => response.json())
      .then(data => {
        setCategories(data);
      })
      .catch(error => console.error('Error fetching categories:', error));
  };

  // Function to determine which star to show
  const getStarImage = (categoryName) => {
    const currentPath = location.pathname.split('/')[2]; // Assuming your URL structure is /site/[categoryName]
    return currentPath === categoryName ? whiteStar : redStar;
  };

  return (
    <div className="navigation">
      <img src={backgroundImage} alt="Background" className="backgroundImageTwo"/>
      <div className="navLinks">
        {categories.map((category, index) => (
          <div key={index} className="linkWithStar">
            <Link to={`/site/${category.c_name}`}>
              {category.c_name_rus}
            </Link>
            <img src={getStarImage(category.c_name)} alt="Star" className="starIcon"/>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Navbar;
