import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import Navigation from '../images/background.png';
import RedStar from '../images/redStar.png'
import WhiteStar from '../images/whiteStar.png'

const Navbar = () => {
    const location = useLocation();
    const [activePage, setActivePage] = useState(""); // State to store active page
    
    // Function to handle page changes
    const handlePageChange = (page) => {
      setActivePage(page);
    };
  
    useEffect(() => {
      // Check if the clicked page is outside of the database section and not "/Links"
      if (!location.pathname.startsWith("/Links") && activePage !== "Links") {
        // If outside of the database section and not "/Links", set activePage to empty string
        setActivePage("");
      }
    }, [location.pathname, activePage]);

  return (
    <div className="navigation">
        <div className="backgroundImageTwo">
            <img src={Navigation} alt="Background Image" />
        </div>
        <div className="navLinks">
            <div className="linkContainer">
                <Link
                    to="/database"
                    className={location.pathname.startsWith("/database") ? "active" : ""}
                >
                    <span className="linkText">База данных</span>
                    <img className="starIcon" src={location.pathname.startsWith("/database") ? RedStar : WhiteStar} alt="Star Icon" />
                </Link>
                {location.pathname.startsWith("/database") && (
                    <>
                    <Link
                        to="/database/search"
                        className="subLink"
                        onClick={() => handlePageChange("Search")}
                    >
                        <span className="linkTextTwo">Поиск в базе данных</span>
                        <img className="starIconTwo" src={activePage === "Search" ? WhiteStar : RedStar} alt="Star Icon" />
                    </Link>
                    </>
                )}
            </div>

            <div className="linkContainer">
                <Link
                    to="/Links"
                    onClick={() => handlePageChange("Links")}
                    className={activePage === "Links" || location.pathname.startsWith("/Links") ? "active" : ""}
                >
                    <span className="linkText">Описание боевых действий</span>
                    <img className="starIcon" src={!location.pathname.startsWith("/Links") ? RedStar : WhiteStar} alt="Star Icon" />
                </Link>
            </div>

            {/* Other Link components */}
        </div>
    </div>
);
};

export default Navbar;
