import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

// Assuming you are making the API call and receiving the data correctly

const ContentPage = () => {
  const { pathName } = useParams();
  const [categoryContent, setCategoryContent] = useState([]);
  const [x, setX] = useState(null);
  const [y, setY] = useState(null);

  useEffect(() => {
    fetchCategoryContent(pathName);
  }, [pathName]);

  const fetchCategoryContent = (pathName) => {
    console.log("Fetching content for pathName:", pathName);
    const path = pathName.startsWith('/') ? pathName : `/${pathName}`; // Ensure path starts with a /
    fetch(`http://localhost:1337/paths${path}`)
      .then(response => response.json())
      .then(data => {
        console.log("Category content:", data);
        setCategoryContent(data.results);
        setX(data.x);
        setY(data.y);
      })
      .catch(error => console.error('Error fetching category content:', error));
  };
  
  

  return (
    <div className="generalPages">
      <p>X: {x}</p>
      <p>Y: {y}</p>
      {categoryContent.map((content, index) => (
        <div key={index}>
          <h2>{content.a_title}</h2>
          <div dangerouslySetInnerHTML={{ __html: content.a_text }}></div>
        </div>
      ))}
    </div>
  );
};


export default ContentPage;
