import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

const ContentPage = () => {
  const { pathName } = useParams(); // Assuming the parameter is named 'pathName'
  const [categoryContent, setCategoryContent] = useState('');

  useEffect(() => {
    fetchCategoryContent(pathName); // Pass pathName to fetchCategoryContent
  }, [pathName]); // Include pathName in the dependency array

  const fetchCategoryContent = (pathName) => {
    console.log("Fetching content for pathName:", pathName); // Log the pathName
    // Fetch category content based on pathName
    fetch(`http://localhost:1337/paths/${pathName}`) // Assuming the endpoint is /paths/:pathName
      .then(response => response.json())
      .then(data => {
        setCategoryContent(data);
      })
      .catch(error => console.error('Error fetching category content:', error));
  };

  return (
    <div className="content">
      <h2>Category Content</h2>
      <p>{categoryContent}</p>
    </div>
  );
};

export default ContentPage;
