import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';

// Navbar.jsx
const Navbar = ({ navHeight }) => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://localhost:1337/categories/1');
      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  // Apply the height dynamically through styles
  const navStyle = {
    height: navHeight ? `${navHeight}px` : 'auto', // Use the navHeight if available
  };

  return (
    <div className="navigation" style={navStyle}>
      <div className="linkContainer">
        {categories.map((category, index) => (
          <Link key={index} to={`/site/${category.c_name}`}>
            {category.c_name_rus}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Navbar;
