import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

const ContentPage = () => {
  const { pathName } = useParams();
  const [pathInfo, setPathInfo] = useState(null);
  const [x, setX] = useState(null);
  const [y, setY] = useState(null);

  useEffect(() => {
    fetchPathInfo(pathName);
  }, [pathName]);

  const fetchPathInfo = (pathName) => {
    // Fetch path information based on pathName
    fetch(`http://localhost:1337/paths/${pathName}`)
      .then(response => response.json())
      .then(data => {
        setPathInfo(data);
        // Extract x and y from p_path
        if (data && data.length > 0 && data[0].p_path) {
          const pathParts = data[0].p_path.split(',');
          if (pathParts.length >= 2) {
            setY(pathParts[pathParts.length - 2]);
            setX(pathParts[pathParts.length - 1]);
          }
        }
      })
      .catch(error => console.error('Error fetching path info:', error));
  };

  return (
    <div className="generalPages">
      <h2>Path Information</h2>
      {pathInfo && pathInfo.length > 0 ? (
        <div>
          <p>p_path: {pathInfo[0].p_path}</p>
          <p>X: {x}</p>
          <p>Y: {y}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ContentPage;
