import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ContentPageMultiple = () => {
  const { param2 } = useParams(); // Assuming param2 is the letter from the URL
  const [content, setContent] = useState([]);

  useEffect(() => {
    const russianLetter = letterMapping[param2.toUpperCase()] || 'А';
    fetchData(russianLetter);
  }, [param2]);

  const fetchData = (russianLetter) => {
    const url = `http://localhost:1337/site/biblio/${encodeURIComponent(russianLetter)}`;
    fetch(url)
      .then(response => response.json())
      .then(data => setContent(data))
      .catch(error => console.error('Error fetching content:', error));
  };

  return (
    <div>
      {content.map((item, index) => (
        <div key={index}>
          <h3>{item.a_title}</h3>
          <p dangerouslySetInnerHTML={{ __html: item.a_text }} />
        </div>
      ))}
    </div>
  );
};

export default ContentPageMultiple;
