import React, { useState, useEffect } from "react";
import { useParams, Link } from 'react-router-dom';
import { contentSinglePathDetails } from './apiConfig';

const ContentPage = () => {
  const { pathName } = useParams();
  const [articles, setArticles] = useState([]);
  const [selectedAbbreviation, setSelectedAbbreviation] = useState(null);

  useEffect(() => {
    // Fetch the first abbreviation article by default
    fetchAbbreviationArticle(8);
    // Fetch all abbreviation articles or letters
    fetchAbbreviationLetters();
  }, []);

  const fetchAbbreviationArticle = (a_id) => {
    fetch(`${contentSinglePathDetails}/article/${a_id}`)
      .then(response => response.json())
      .then(data => {
        setSelectedAbbreviation(data);
      })
      .catch(error => console.error('Error fetching abbreviation article:', error));
  };

  const fetchAbbreviationLetters = () => {
    // Assuming you have an endpoint to fetch all abbreviations
    fetch(`${contentSinglePathDetails}/abbreviations`)
      .then(response => response.json())
      .then(data => {
        setArticles(data);
      })
      .catch(error => console.error('Error fetching abbreviation letters:', error));
  };

  return (
    <div className="generalPages">
      {selectedAbbreviation && (
        <div>
          <h2>{selectedAbbreviation.a_title}</h2>
          <div dangerouslySetInnerHTML={{ __html: selectedAbbreviation.a_text }}></div>
        </div>
      )}
      <div>
        {articles.map((article, index) => (
          <Link key={index} to={`/site/abbreviation/${article.letter}`} onClick={() => fetchAbbreviationArticle(article.a_id)}>
            {article.letter}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default ContentPage;
