import React, { useState, useEffect } from 'react';
import { useParams, useLocation, Link } from 'react-router-dom';

const ContentPageMultiple = () => {
  const { param1, param2 } = useParams();
  const location = useLocation();
  const [content, setContent] = useState([]);
  const [pageTitle, setPageTitle] = useState('');
  
  const letterMappingAbbreviations = {
  A: 'А', B: 'Б', V: 'В', G: 'Г', D: 'Д', J: 'Е', ZH: 'Ж', Z: 'З',
  I: 'И', K: 'К', L: 'Л', M: 'М', N: 'Н', O: 'О', P: 'П', R: 'Р',
  S: 'С', T: 'Т', U: 'У', F: 'Ф', H: 'Х', C: 'Ц', CH: 'Ч', SH: 'Ш',
  SCH: 'Щ', Y: 'Ы', E: 'Э', YU: 'Ю', YA: 'Я', };
  const letterMappingBibliography = {
    A: 'А', B: 'Б', V: 'В', G: 'Г', D: 'Д', E: 'Е', ZH: 'Ж', Z: 'З',
    I: 'И', K: 'К', L: 'Л', M: 'М', N: 'Н', O: 'О', P: 'П', R: 'Р',
    S: 'С', T: 'Т', U: 'У', F: 'Ф', H: 'Х', C: 'Ц', CH: 'Ч', SH: 'Ш',
    SCH: 'Щ', Y: 'Ы', YE: 'Э', YU: 'Ю', YA: 'Я', };

    useEffect(() => {
      const fetchContent = async () => {
        let url = '';
    
        if (location.pathname.includes('/site/article/')) {
          fetchArticleByAId(param2); // Assuming param2 is the article ID
        } else {
          if (location.pathname.includes('biblio') || location.pathname.includes('abbreviation')) {
            const russianLetter = location.pathname.includes('abbreviation') ? 
                                  letterMappingAbbreviations[param2.toUpperCase()] : 
                                  letterMappingBibliography[param2.toUpperCase()];
            url = `http://localhost:1337/site/${param1}/${russianLetter}`;
          } else {
            // For generic paths that include two parameters
            url = `http://localhost:1337/paths/${param1}/${param2}`;
          }
    
          // Special handling for the specific URL
          const specialURL = '/site/commander/front/';
          const titleQueryParam = new URLSearchParams(location.search).get('title');
          if (location.pathname === specialURL && titleQueryParam === 'Командующие+Карельским+фронтом') {
            // In this case, fetch the article to set its title as pageTitle
            fetchArticleByAId(param2).then(data => {
              if (data && data.a_title) {
                setPageTitle(data.a_title);
              }
            });
          } else {
            // Attempt to set pageTitle based on URL path or query parameter
            const queryParams = new URLSearchParams(location.search);
            const titleFromQuery = queryParams.get('title');
            if (titleFromQuery) {
              setPageTitle(titleFromQuery);
            } else {
              setPageTitle(param2.charAt(0).toUpperCase() + param2.slice(1)); // Fallback title
            }
          }
        }
    
        try {
          const response = await fetch(url);
          const data = await response.json();
          setContent(data);
        } catch (error) {
          console.error('Error fetching content:', error);
        }
      };
    
      fetchContent();
    }, [param1, param2, location.pathname, location.search]);
    
    

  const fetchAbbreviations = (letter) => {
    const russianLetter = letterMappingAbbreviations[letter.toUpperCase()] || letter;
    fetch(`http://localhost:1337/site/abbreviation/${russianLetter}`)
      .then(response => response.json())
      .then(data => {
        console.log(`Fetching from URL: http://localhost:1337/site/abbreviation/${russianLetter}`);
        setContent(data);
      })
      .catch(error => console.error('Error fetching abbreviations:', error));
  };
  
  const fetchArticleByAId = async (aId) => {
    try {
      const response = await fetch(`http://localhost:1337/site/article/${aId}`);
      const data = await response.json();
      if (location.pathname.includes('/site/article/')) {
        // If we're on a specific article page, set the pageTitle to that article's title.
        setPageTitle(data.a_title);
      }
      setContent([data]); // Setting content with an array containing the fetched article
    } catch (error) {
      console.error('Error fetching article by ID:', error);
    }
  };

  const fetchBibliographyArticles = (letter) => {
    // Assuming letterMapping translates English letter to Russian for fetching
    const russianLetter = letterMappingBibliography[letter.toUpperCase()] || letter;
    fetch(`http://localhost:1337/site/biblio/${russianLetter}`)
      .then(response => response.json())
      .then(data => {
        setContent(data);
      })
      .catch(error => console.error('Error fetching bibliography articles:', error));
  };

  return (
    <div className="generalPages">
      <h1>{pageTitle}</h1> {/* Display the page title */}
      {content.map((item, index) => (
        <div key={index}>
          {location.pathname.includes('/site/article/') ? (
            <h3 dangerouslySetInnerHTML={{ __html: item.a_title }} />
          ) : (
            <Link to={`/site/article/${item.a_id}`}>
              <h3 dangerouslySetInnerHTML={{ __html: item.a_title }} />
            </Link>
          )}
          <div className="desc" dangerouslySetInnerHTML={{ __html: item.a_desc || item.a_text }} />
        </div>
      ))}
    </div>
  );
};

export default ContentPageMultiple;
