import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

const ContentPage = () => {
  const { pathName } = useParams();
  const [pathInfo, setPathInfo] = useState(null);
  const [articleText, setArticleText] = useState(null);

  useEffect(() => {
    fetchPathDetails(pathName);
  }, [pathName]);

  const fetchPathDetails = (pathName) => {
    fetch(`http://localhost:1337/path-details/${pathName}`)
      .then(response => response.json())
      .then(data => {
        // console.log('Path Details from Backend:', data); // Log the data received from the backend
        setPathInfo(data);
        // Fetch article details if c_id is available
        if (data && data.c_id) {
          // Set article text directly using the c_id
          setArticleText(data.c_id.toString());
        } else {
          // If c_id is not available, set a default message
          setArticleText("No article text available");
        }
      })
      .catch(error => console.error('Error fetching path details:', error));
  };
  

  const fetchArticleDetails = (categoryId) => {
    fetch(`http://localhost:1337/article-details/${categoryId}`)
      .then(response => response.json())
      .then(data => {
        console.log('Article Details from Backend:', data); // Log the data received from the backend
        if (data.text) {
          console.log('Setting article text:', data.text); // Log the article text being set
          setArticleText(data.text);
        } else {
          console.log('No article text available');
          setArticleText("No article text available"); // Set default message if no article text is received
        }
      })
      .catch(error => console.error('Error fetching article details:', error));
  };
  
  
  return (
    <div className="generalPages">
      {pathInfo ? (
        <div>
          <h2>{pathInfo.title}</h2>
          {articleText ? <p>{articleText}</p> : <p>Loading article text...</p>}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ContentPage;
