import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import Navigation from '../images/background.png';
import RedStar from '../images/redStar.png'
import WhiteStar from '../images/whiteStar.png'

const Navbar = () => {
  const location = useLocation();
  const [activePage, setActivePage] = useState(""); // State to store active page
  const [showDatabaseSublinks, setShowDatabaseSublinks] = useState(false);
  
  // Function to handle page changes
  const handlePageChange = (page) => {
    setActivePage(page);
  };

  const toggleDatabaseSublinks = () => {
    setShowDatabaseSublinks(!showDatabaseSublinks);
  };

  // Clear active page when navigating back to the main page ("/")
  useEffect(() => {
    if (!location.pathname.startsWith("/About") && !location.pathname.startsWith("/Search")) {
      setShowDatabaseSublinks(false);
    }
  }, [location.pathname]);
  

  return (
    <div className="navigation">
        <div className="backgroundImageTwo">
            <img src={Navigation} alt="Background Image" />
        </div>
        <div className="navLinks">
        <div className="linkContainer">
          <Link
            to="/About"
            onClick={toggleDatabaseSublinks}
            className={showDatabaseSublinks ? "active" : ""}
          >
            <span className="linkText">База данных</span>
        <img className="starIcon" src={showDatabaseSublinks ? WhiteStar : RedStar} alt="Star Icon" />
          </Link>
          {/* Conditionally render sub-links based on visibility */}
          {showDatabaseSublinks && (
            <>
              <Link
                to="/Search"
                className="subLink"
                onClick={() => handlePageChange("Search")}
              >
                <span className="linkTextTwo">Поиск в базе данных</span>
              </Link>
              {/* Add other sub-links here */}
            </>
          )}
        </div>

            <div className="linkContainer">
                <Link
                    to="/Links"
                    onClick={() => handlePageChange("Links")}
                    className={activePage === "Links" ? "active" : ""}
                >
                    <span className="linkText">Описание боевых действий</span>
                    <img className="starIcon" src={activePage === "Links" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>
            </div>

            <div className="linkContainer">
                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkText">Список частей и соединений</span>
                    <img className="starIcon" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>
                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkTextTwo">Армии</span>
                    <img className="starIconThree" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>
                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkTextTwo">Корпуса</span>
                    <img className="starIconFour" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>
                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkTextTwo">Дивизии</span>
                    <img className="starIconFive" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>
            </div>

            <div className="linkContainer">
                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkText">Биографии командиров</span>
                    <img className="starIcon" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>

                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkTextTwo">Командующие Карельским фронтом</span>
                    <img className="starIconTwo" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>

                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkTextTwo">Командующие армиями</span>
                    <img className="starIconFour" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>

                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkTextTwo">Командиры корпусов</span>
                    <img className="starIconFive" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>

                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkTextTwo">Командиры дивизий</span>
                    <img className="starIconSix" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>

            </div>

            <div className="linkContainer">
                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkText">Герои Советского Союза</span>
                    <img className="starIcon" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>
            </div>


            <div className="linkContainer">
                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkText">Оккупационный режим</span>
                    <img className="starIcon" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>
                <Link
                    to="/Feedback"
                    onClick={() => handlePageChange("Feedback")}
                    className={activePage === "Feedback" ? "active" : ""}
                >
                    <span className="linkTextTwo">Воспоминания</span>
                    <img className="starIconTwo" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
                </Link>
            </div>



            <div className="linkContainer">
            <Link
                to="/Feedback"
                onClick={() => handlePageChange("Feedback")}
                className={activePage === "Feedback" ? "active" : ""}
            >
                <span className="linkText">Партизаны</span>
                <img className="starIcon" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
            </Link>
            </div>

            <div className="linkContainer">
            <Link
                to="/Feedback"
                onClick={() => handlePageChange("Feedback")}
                className={activePage === "Feedback" ? "active" : ""}
            >
                <span className="linkText">Эвакуация гражданского населения</span>
                <img className="starIcon" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
            </Link>
            </div>




            <div className="linkContainer">
            <Link
                to="/Feedback"
                onClick={() => handlePageChange("Feedback")}
                className={activePage === "Feedback" ? "active" : ""}
            >
                <span className="linkText">Библиография</span>
                <img className="starIcon" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
            </Link>
            </div>


            <div className="linkContainer">
            <Link
                to="/Feedback"
                onClick={() => handlePageChange("Feedback")}
                className={activePage === "Feedback" ? "active" : ""}
            >
                <span className="linkText">Литература о войне</span>
                <img className="starIcon" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
            </Link>
            </div>

            <div className="linkContainer">
            <Link
                to="/Feedback"
                onClick={() => handlePageChange("Feedback")}
                className={activePage === "Feedback" ? "active" : ""}
            >
                <span className="linkText">Список сокращений</span>
                <img className="starIcon" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
            </Link>
            </div>


            <div className="linkContainer">
            <Link
                to="/Feedback"
                onClick={() => handlePageChange("Feedback")}
                className={activePage === "Feedback" ? "active" : ""}
            >
                <span className="linkText">Фотографии</span>
                <img className="starIcon" src={activePage === "Feedback" ? WhiteStar : RedStar} alt="Star Icon" />
            </Link>
            </div>









        </div>

    </div>
);
};

export default Navbar;
