import React, { useState, useEffect } from "react";
import { Link, useLocation } from 'react-router-dom';
import backgroundImage from '../images/background.png';
import whiteStar from '../images/whiteStar.png';
import redStar from '../images/redStar.png';

const Navbar = () => {
  const [categories, setCategories] = useState([]);
  const [activeCategoryId, setActiveCategoryId] = useState(null); // State to keep track of the active category ID
  const [subCategories, setSubCategories] = useState([]); // State for storing subcategories of the active category
  const location = useLocation();

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = () => {
    fetch('http://localhost:1337/categories/1')
      .then(response => response.json())
      .then(data => {
        setCategories(data);
      })
      .catch(error => console.error('Error fetching categories:', error));
  };

  // Function to fetch subcategories when a category is clicked
  const handleCategoryClick = (categoryId) => {
    // Set the clicked category as active
    setActiveCategoryId(categoryId);

    // Fetch subcategories of the clicked category
    fetch(`http://localhost:1337/categories/${categoryId}`)
      .then(response => response.json())
      .then(data => {
        setSubCategories(data);
      })
      .catch(error => console.error('Error fetching subcategories:', error));
  };

  const currentPath = location.pathname.split('/')[2];

  const getStarImage = (categoryName) => {
    return currentPath === categoryName ? whiteStar : redStar;
  };

  return (
    <div className="navigation">
      <img src={backgroundImage} alt="Background" className="backgroundImageTwo"/>
      <div className="navLinks">
        {categories.map((category, index) => (
          <div key={index} className="linkWithStar" onClick={() => handleCategoryClick(category.c_id)}>
            <Link to={`/site/${category.c_name}`} className="navLink">
              {category.c_name_rus}
            </Link>
            <img src={getStarImage(category.c_name)} alt="Star" className={currentPath === category.c_name ? "starIconWhite" : "starIconRed"} />
          </div>
        ))}
        {activeCategoryId && subCategories.map((subCategory, index) => (
          <Link key={index} to={`/site/${subCategory.c_name}`} className="navLink">
            {subCategory.c_name_rus}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Navbar;
