import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

const ContentPage = () => {
  const { pathName } = useParams();
  const [articles, setArticles] = useState([]); // State to store article data

  useEffect(() => {
    fetchArticleData(pathName);
  }, [pathName]);

  const fetchArticleData = (pathName) => {
    fetch(`http://localhost:1337/paths/${pathName}`)
      .then(response => response.json())
      .then(data => {
        if (data && data.length > 0) {
          const categoryId = data[0].c_id;
          fetchArticles(categoryId); // Fetch articles based on category ID
        }
      })
      .catch(error => console.error('Error fetching category content:', error));
  };

  const fetchArticles = (categoryId) => {
    fetch(`http://localhost:1337/articles/${categoryId}`) // Assuming the endpoint is /articles/:categoryId
      .then(response => response.json())
      .then(data => {
        setArticles(data); // Set the fetched articles
      })
      .catch(error => console.error('Error fetching articles:', error));
  };

  return (
    <div className="generalPages">
      {articles.map(article => (
        <div key={article.a_id}>
          <h2>{article.a_title}</h2>
          <p>{article.a_text}</p>
        </div>
      ))}
    </div>
  );
};

export default ContentPage;
