import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

const ContentPage = () => {
  const { pathName } = useParams();
  const [categoryContent, setCategoryContent] = useState('');

  useEffect(() => {
    fetchCategoryContent(pathName);
  }, [pathName]);

  const fetchCategoryContent = (pathName) => {
    console.log("Fetching content for pathName:", pathName);
    const pathParts = pathName.split('/'); // Split the pathName by '/'
    const lastPathPart = pathParts[pathParts.length - 1]; // Get the last part of the path
    const newPathName = lastPathPart ? `/${lastPathPart}/` : ''; // Construct the new pathName
    console.log("New pathName:", newPathName);
    fetch(`http://localhost:1337/paths${newPathName}`) // Pass the new pathName to the API
      .then(response => response.json())
      .then(data => {
        console.log("Category content:", data);
        setCategoryContent(data);
      })
      .catch(error => console.error('Error fetching category content:', error));
  };

  console.log("Render categoryContent:", categoryContent);

  return (
    <div className="generalPages">
      {categoryContent.map((content, index) => (
        <div key={index}>
          <h2>{content.a_title}</h2>
          <div dangerouslySetInnerHTML={{ __html: content.a_text }}></div>
        </div>
      ))}
    </div>
  );
};

export default ContentPage;
