import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
// Assuming the images folder is at the root of your project and Navbar.jsx is inside /src/components
import backgroundImage from '../images/background.png'; // Adjust the path as needed

const Navbar = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = () => {
    fetch('http://localhost:1337/categories/1')
      .then(response => response.json())
      .then(data => {
        setCategories(data);
      })
      .catch(error => console.error('Error fetching categories:', error));
  };

  // Apply the background image to the navigation div
  const navigationStyle = {
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover', // Cover the entire area of the div
    backgroundPosition: 'center', // Center the background image
    height: "800px",
    opacity: 1, // Adjust opacity as needed
  };

  return (
    <div className="navigation" style={navigationStyle}>
      <div className="linkContainer">
        {categories.map(category => (
          <Link
            key={category.c_name}
            to={`/site/${category.c_name}`}
          >
            {category.c_name_rus}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Navbar;
